const size = [
  "xs",
  "sm",
  "md",
  "lg",
  "xl"
] as const

const variant = [
  "outline",
  "soft",
  "subtle",
  "ghost",
  "none"
] as const

const color = [
  "primary",
  "secondary",
  "success",
  "info",
  "warning",
  "error",
  "neutral"
] as const

export default {
  "slots": {
    "root": "relative inline-flex items-center",
    "base": [
      "w-full rounded-md border-0 appearance-none placeholder:text-dimmed focus:outline-none disabled:cursor-not-allowed disabled:opacity-75",
      "transition-colors"
    ],
    "leading": "absolute start-0 flex items-start",
    "leadingIcon": "shrink-0 text-dimmed",
    "leadingAvatar": "shrink-0",
    "leadingAvatarSize": "",
    "trailing": "absolute end-0 flex items-start",
    "trailingIcon": "shrink-0 text-dimmed"
  },
  "variants": {
    "fieldGroup": {
      "horizontal": {
        "root": "group has-focus-visible:z-[1]",
        "base": "group-not-only:group-first:rounded-e-none group-not-only:group-last:rounded-s-none group-not-last:group-not-first:rounded-none"
      },
      "vertical": {
        "root": "group has-focus-visible:z-[1]",
        "base": "group-not-only:group-first:rounded-b-none group-not-only:group-last:rounded-t-none group-not-last:group-not-first:rounded-none"
      }
    },
    "size": {
      "xs": {
        "base": "px-2 py-1 text-xs gap-1",
        "leading": "ps-2 inset-y-1",
        "trailing": "pe-2 inset-y-1",
        "leadingIcon": "size-4",
        "leadingAvatarSize": "3xs",
        "trailingIcon": "size-4"
      },
      "sm": {
        "base": "px-2.5 py-1.5 text-xs gap-1.5",
        "leading": "ps-2.5 inset-y-1.5",
        "trailing": "pe-2.5 inset-y-1.5",
        "leadingIcon": "size-4",
        "leadingAvatarSize": "3xs",
        "trailingIcon": "size-4"
      },
      "md": {
        "base": "px-2.5 py-1.5 text-sm gap-1.5",
        "leading": "ps-2.5 inset-y-1.5",
        "trailing": "pe-2.5 inset-y-1.5",
        "leadingIcon": "size-5",
        "leadingAvatarSize": "2xs",
        "trailingIcon": "size-5"
      },
      "lg": {
        "base": "px-3 py-2 text-sm gap-2",
        "leading": "ps-3 inset-y-2",
        "trailing": "pe-3 inset-y-2",
        "leadingIcon": "size-5",
        "leadingAvatarSize": "2xs",
        "trailingIcon": "size-5"
      },
      "xl": {
        "base": "px-3 py-2 text-base gap-2",
        "leading": "ps-3 inset-y-2",
        "trailing": "pe-3 inset-y-2",
        "leadingIcon": "size-6",
        "leadingAvatarSize": "xs",
        "trailingIcon": "size-6"
      }
    },
    "variant": {
      "outline": "text-highlighted bg-default ring ring-inset ring-accented",
      "soft": "text-highlighted bg-elevated/50 hover:bg-elevated focus:bg-elevated disabled:bg-elevated/50",
      "subtle": "text-highlighted bg-elevated ring ring-inset ring-accented",
      "ghost": "text-highlighted bg-transparent hover:bg-elevated focus:bg-elevated disabled:bg-transparent dark:disabled:bg-transparent",
      "none": "text-highlighted bg-transparent"
    },
    "color": {
      "primary": "",
      "secondary": "",
      "success": "",
      "info": "",
      "warning": "",
      "error": "",
      "neutral": ""
    },
    "leading": {
      "true": ""
    },
    "trailing": {
      "true": ""
    },
    "loading": {
      "true": ""
    },
    "highlight": {
      "true": ""
    },
    "type": {
      "file": "file:me-1.5 file:font-medium file:text-muted file:outline-none"
    },
    "autoresize": {
      "true": {
        "base": "resize-none"
      }
    }
  },
  "compoundVariants": [
    {
      "color": "primary" as typeof color[number],
      "variant": [
        "outline" as typeof variant[number],
        "subtle" as typeof variant[number]
      ],
      "class": "focus-visible:ring-2 focus-visible:ring-inset focus-visible:ring-primary"
    },
    {
      "color": "secondary" as typeof color[number],
      "variant": [
        "outline" as typeof variant[number],
        "subtle" as typeof variant[number]
      ],
      "class": "focus-visible:ring-2 focus-visible:ring-inset focus-visible:ring-secondary"
    },
    {
      "color": "success" as typeof color[number],
      "variant": [
        "outline" as typeof variant[number],
        "subtle" as typeof variant[number]
      ],
      "class": "focus-visible:ring-2 focus-visible:ring-inset focus-visible:ring-success"
    },
    {
      "color": "info" as typeof color[number],
      "variant": [
        "outline" as typeof variant[number],
        "subtle" as typeof variant[number]
      ],
      "class": "focus-visible:ring-2 focus-visible:ring-inset focus-visible:ring-info"
    },
    {
      "color": "warning" as typeof color[number],
      "variant": [
        "outline" as typeof variant[number],
        "subtle" as typeof variant[number]
      ],
      "class": "focus-visible:ring-2 focus-visible:ring-inset focus-visible:ring-warning"
    },
    {
      "color": "error" as typeof color[number],
      "variant": [
        "outline" as typeof variant[number],
        "subtle" as typeof variant[number]
      ],
      "class": "focus-visible:ring-2 focus-visible:ring-inset focus-visible:ring-error"
    },
    {
      "color": "primary" as typeof color[number],
      "highlight": true,
      "class": "ring ring-inset ring-primary"
    },
    {
      "color": "secondary" as typeof color[number],
      "highlight": true,
      "class": "ring ring-inset ring-secondary"
    },
    {
      "color": "success" as typeof color[number],
      "highlight": true,
      "class": "ring ring-inset ring-success"
    },
    {
      "color": "info" as typeof color[number],
      "highlight": true,
      "class": "ring ring-inset ring-info"
    },
    {
      "color": "warning" as typeof color[number],
      "highlight": true,
      "class": "ring ring-inset ring-warning"
    },
    {
      "color": "error" as typeof color[number],
      "highlight": true,
      "class": "ring ring-inset ring-error"
    },
    {
      "color": "neutral" as typeof color[number],
      "variant": [
        "outline" as typeof variant[number],
        "subtle" as typeof variant[number]
      ],
      "class": "focus-visible:ring-2 focus-visible:ring-inset focus-visible:ring-inverted"
    },
    {
      "color": "neutral" as typeof color[number],
      "highlight": true,
      "class": "ring ring-inset ring-inverted"
    },
    {
      "leading": true,
      "size": "xs" as typeof size[number],
      "class": "ps-7"
    },
    {
      "leading": true,
      "size": "sm" as typeof size[number],
      "class": "ps-8"
    },
    {
      "leading": true,
      "size": "md" as typeof size[number],
      "class": "ps-9"
    },
    {
      "leading": true,
      "size": "lg" as typeof size[number],
      "class": "ps-10"
    },
    {
      "leading": true,
      "size": "xl" as typeof size[number],
      "class": "ps-11"
    },
    {
      "trailing": true,
      "size": "xs" as typeof size[number],
      "class": "pe-7"
    },
    {
      "trailing": true,
      "size": "sm" as typeof size[number],
      "class": "pe-8"
    },
    {
      "trailing": true,
      "size": "md" as typeof size[number],
      "class": "pe-9"
    },
    {
      "trailing": true,
      "size": "lg" as typeof size[number],
      "class": "pe-10"
    },
    {
      "trailing": true,
      "size": "xl" as typeof size[number],
      "class": "pe-11"
    },
    {
      "loading": true,
      "leading": true,
      "class": {
        "leadingIcon": "animate-spin"
      }
    },
    {
      "loading": true,
      "leading": false,
      "trailing": true,
      "class": {
        "trailingIcon": "animate-spin"
      }
    }
  ],
  "defaultVariants": {
    "size": "md" as typeof size[number],
    "color": "primary" as typeof color[number],
    "variant": "outline" as typeof variant[number]
  }
}
<script>
import theme from "#build/ui/textarea";
</script>

<script setup>
import { useTemplateRef, computed, onMounted, nextTick, watch } from "vue";
import { Primitive } from "reka-ui";
import { useVModel } from "@vueuse/core";
import { useAppConfig } from "#imports";
import { useComponentIcons } from "../composables/useComponentIcons";
import { useFormField } from "../composables/useFormField";
import { looseToNumber } from "../utils";
import { tv } from "../utils/tv";
import UIcon from "./Icon.vue";
import UAvatar from "./Avatar.vue";
defineOptions({ inheritAttrs: false });
const props = defineProps({
  as: { type: null, required: false },
  id: { type: String, required: false },
  name: { type: String, required: false },
  placeholder: { type: String, required: false },
  color: { type: null, required: false },
  variant: { type: null, required: false },
  size: { type: null, required: false },
  required: { type: Boolean, required: false },
  autofocus: { type: Boolean, required: false },
  autofocusDelay: { type: Number, required: false, default: 0 },
  autoresize: { type: Boolean, required: false },
  autoresizeDelay: { type: Number, required: false, default: 0 },
  disabled: { type: Boolean, required: false },
  rows: { type: Number, required: false, default: 3 },
  maxrows: { type: Number, required: false, default: 0 },
  highlight: { type: Boolean, required: false },
  modelValue: { type: null, required: false },
  defaultValue: { type: null, required: false },
  modelModifiers: { type: Object, required: false },
  class: { type: null, required: false },
  ui: { type: null, required: false },
  icon: { type: null, required: false },
  avatar: { type: Object, required: false },
  leading: { type: Boolean, required: false },
  leadingIcon: { type: null, required: false },
  trailing: { type: Boolean, required: false },
  trailingIcon: { type: null, required: false },
  loading: { type: Boolean, required: false },
  loadingIcon: { type: null, required: false }
});
const emits = defineEmits(["update:modelValue", "blur", "change"]);
const slots = defineSlots();
const modelValue = useVModel(props, "modelValue", emits, { defaultValue: props.defaultValue });
const appConfig = useAppConfig();
const { emitFormFocus, emitFormBlur, emitFormInput, emitFormChange, size, color, id, name, highlight, disabled, ariaAttrs } = useFormField(props, { deferInputValidation: true });
const { isLeading, isTrailing, leadingIconName, trailingIconName } = useComponentIcons(props);
const ui = computed(() => tv({ extend: tv(theme), ...appConfig.ui?.textarea || {} })({
  color: color.value,
  variant: props.variant,
  size: size?.value,
  loading: props.loading,
  highlight: highlight.value,
  autoresize: props.autoresize,
  leading: isLeading.value || !!props.avatar || !!slots.leading,
  trailing: isTrailing.value || !!slots.trailing
}));
const textareaRef = useTemplateRef("textareaRef");
function updateInput(value) {
  if (props.modelModifiers?.trim) {
    value = value?.trim() ?? null;
  }
  if (props.modelModifiers?.number) {
    value = looseToNumber(value);
  }
  if (props.modelModifiers?.nullable) {
    value ||= null;
  }
  if (props.modelModifiers?.optional) {
    value ||= void 0;
  }
  modelValue.value = value;
  emitFormInput();
}
function onInput(event) {
  autoResize();
  if (!props.modelModifiers?.lazy) {
    updateInput(event.target.value);
  }
}
function onChange(event) {
  const value = event.target.value;
  if (props.modelModifiers?.lazy) {
    updateInput(value);
  }
  if (props.modelModifiers?.trim) {
    event.target.value = value.trim();
  }
  emitFormChange();
  emits("change", event);
}
function onBlur(event) {
  emitFormBlur();
  emits("blur", event);
}
function autoFocus() {
  if (props.autofocus) {
    textareaRef.value?.focus();
  }
}
function autoResize() {
  if (props.autoresize && textareaRef.value) {
    textareaRef.value.rows = props.rows;
    const overflow = textareaRef.value.style.overflow;
    textareaRef.value.style.overflow = "hidden";
    const styles = window.getComputedStyle(textareaRef.value);
    const paddingTop = Number.parseInt(styles.paddingTop);
    const paddingBottom = Number.parseInt(styles.paddingBottom);
    const padding = paddingTop + paddingBottom;
    const lineHeight = Number.parseInt(styles.lineHeight);
    const { scrollHeight } = textareaRef.value;
    const newRows = (scrollHeight - padding) / lineHeight;
    if (newRows > props.rows) {
      textareaRef.value.rows = props.maxrows ? Math.min(newRows, props.maxrows) : newRows;
    }
    textareaRef.value.style.overflow = overflow;
  }
}
watch(modelValue, () => {
  nextTick(autoResize);
});
onMounted(() => {
  setTimeout(() => {
    autoFocus();
  }, props.autofocusDelay);
  setTimeout(() => {
    autoResize();
  }, props.autoresizeDelay);
});
defineExpose({
  textareaRef
});
</script>

<template>
  <Primitive :as="as" data-slot="root" :class="ui.root({ class: [props.ui?.root, props.class] })">
    <textarea
      :id="id"
      ref="textareaRef"
      :value="modelValue"
      :name="name"
      :rows="rows"
      :placeholder="placeholder"
      data-slot="base"
      :class="ui.base({ class: props.ui?.base })"
      :disabled="disabled"
      :required="required"
      v-bind="{ ...$attrs, ...ariaAttrs }"
      @input="onInput"
      @blur="onBlur"
      @change="onChange"
      @focus="emitFormFocus"
    />

    <slot :ui="ui" />

    <span v-if="isLeading || !!avatar || !!slots.leading" data-slot="leading" :class="ui.leading({ class: props.ui?.leading })">
      <slot name="leading" :ui="ui">
        <UIcon v-if="isLeading && leadingIconName" :name="leadingIconName" data-slot="leadingIcon" :class="ui.leadingIcon({ class: props.ui?.leadingIcon })" />
        <UAvatar v-else-if="!!avatar" :size="props.ui?.leadingAvatarSize || ui.leadingAvatarSize()" v-bind="avatar" data-slot="leadingAvatar" :class="ui.leadingAvatar({ class: props.ui?.leadingAvatar })" />
      </slot>
    </span>

    <span v-if="isTrailing || !!slots.trailing" data-slot="trailing" :class="ui.trailing({ class: props.ui?.trailing })">
      <slot name="trailing" :ui="ui">
        <UIcon v-if="trailingIconName" :name="trailingIconName" data-slot="trailingIcon" :class="ui.trailingIcon({ class: props.ui?.trailingIcon })" />
      </slot>
    </span>
  </Primitive>
</template>
export const suggestCategories = async (q) => {
  const token = useCookie('auth_token')
  let bearer = token.value || ''
  try {
    bearer = decodeURIComponent(bearer)
  } catch {
    // keep original if decoding fails
  }

  if (!q || String(q).length < 2) return []

  const url = `http://localhost:8000/api/categories/suggest?q=${encodeURIComponent(q)}`
  const response = await fetch(url, {
    method: 'GET',
    headers: {
      Accept: 'application/json',
      'Content-Type': 'application/json',
      Authorization: `Bearer ${bearer}`
    }
  })

  const raw = await response.json()
  if (!response.ok) throw raw

  // Expecting { data: [ { id, name }, ... ] }
  if (Array.isArray(raw)) return raw
  if (Array.isArray(raw.data)) return raw.data
  if (Array.isArray(raw.categories)) return raw.categories
  return []
}

export default suggestCategories
export const createTicket = async ({ title, description, priority, status, category_id, submitter_id, assigned_to_id, attachments }) => {
  const token = useCookie('auth_token')
  let bearer = token.value || ''
  try {
    bearer = decodeURIComponent(bearer)
  } catch {
    // keep original if decoding fails
  }
  const role = useCookie('user_role').value
  const allowedRoles = ['user', 'technician', 'supervisor', 'admin']
  if (role && !allowedRoles.includes(role)) {
    throw new Error('Invalid role. Allowed roles: user, technician, supervisor, admin')
  }


  const formData = new FormData();
  formData.append('title', title);
  if (description) formData.append('description', description);
  formData.append('priority', priority);
  if (status) formData.append('status', status);
  if (category_id) formData.append('category_id', category_id);
  if (submitter_id) formData.append('submitter_id', submitter_id);
  if (assigned_to_id) formData.append('assigned_tech_id', assigned_to_id);
  if (attachments && attachments.length) {
    attachments.forEach(file => {
      formData.append('attachments[]', file);
    });
  }

  const response = await fetch('http://localhost:8000/api/tickets', {
    method: 'POST',
    headers: {
      Accept: 'application/json',
      Authorization: `Bearer ${bearer}`
      // Note: Do not set Content-Type, browser will set it with boundary
    },
    body: formData
  })

  const data = await response.json()
  if (!response.ok) {
    throw data
  }

  return data
}
<script setup lang="ts">
import { reactive, ref, watch } from 'vue'
import suggestCategories from '~/api/categories/suggest'
import { createTicket } from '~/api/tickets/createTicket'
import { useRouter } from '#imports'


const form = reactive({
	title: '',
	description: '',
    priority: '',
    status: '',
    category_id: null,
    submitter_id: null,
    assigned_to_id: null
})


const priorityOptions = ref([
  'low', 'medium', 'critical'
])



const isSubmitting = ref(false)
const router = useRouter()

const attachments = ref([])
const previews = ref([])
const dragActive = ref(false)
const uploadError = ref('')
const categorySearch = ref('')
const categoryOptions = ref([])
const categoryLoading = ref(false)
let categoryDebounce: ReturnType<typeof setTimeout> | null = null
const selectedCategoryId = ref<number|null>(null)
const selectedCategoryName = ref('')
let titleDebounce: ReturnType<typeof setTimeout> | null = null
const updatePreviews = () => {
	previews.value = attachments.value.map(file => {
		if (file.type.startsWith('image/')) {
			return { url: URL.createObjectURL(file), type: 'image', name: file.name };
		} else if (file.type === 'application/pdf') {
			return { url: URL.createObjectURL(file), type: 'pdf', name: file.name };
		}
		return { url: '', type: 'other', name: file.name };
	});
}
const handleFileChange = (event) => {
	const files = Array.from(event.target.files || [])
	const invalid = files.find(f => f.size > 10 * 1024 * 1024)
	if (invalid) {
		uploadError.value = 'Each file must be less than 10MB.'
		attachments.value = []
		previews.value = []
		return
	}
	uploadError.value = ''
	attachments.value = files
	updatePreviews()
}
const handleDrop = (event) => {
	event.preventDefault()
	dragActive.value = false
	if (event.dataTransfer?.files) {
		const files = Array.from(event.dataTransfer.files)
		const invalid = files.find(f => f.size > 10 * 1024 * 1024)
		if (invalid) {
			uploadError.value = 'Each file must be less than 10MB.'
			attachments.value = []
			previews.value = []
			return
		}
		uploadError.value = ''
		attachments.value = files
		updatePreviews()
	}
}
const handleDragOver = (event) => {
	event.preventDefault()
	dragActive.value = true
}
const handleDragLeave = (event) => {
	event.preventDefault()
	dragActive.value = false
}
const searchCategories = async (q: string) => {
	if (!q || q.length < 2) {
		categoryOptions.value = []
		return
	}
	categoryLoading.value = true
	try {
		const res = await suggestCategories(q)
		categoryOptions.value = Array.isArray(res) ? res : (res.data || res.categories || [])
	} catch (err) {
		categoryOptions.value = []
	} finally {
		categoryLoading.value = false
	}
}

const onCategoryInput = (e) => {
	categorySearch.value = e.target.value
	// clear previously selected id when user types
	selectedCategoryId.value = null
	selectedCategoryName.value = ''
	if (categoryDebounce) clearTimeout(categoryDebounce)
	categoryDebounce = setTimeout(() => searchCategories(categorySearch.value), 300)
}

// Suggest categories from the ticket title (debounced). Only run when user
// hasn't already selected a category.
watch(() => form.title, (val) => {
	if (selectedCategoryId.value) return
	if (titleDebounce) clearTimeout(titleDebounce)
	titleDebounce = setTimeout(() => {
		if (val && String(val).length >= 2) {
			// populate suggestions based on title text but don't overwrite user's
			// category input until they select one
			searchCategories(String(val))
		} else {
			categoryOptions.value = []
		}
	}, 300)
})

const selectCategory = (c) => {
	const id = c?.id ?? c?.value ?? null
	selectedCategoryId.value = id
	selectedCategoryName.value = c?.name || c?.title || c?.label || ''
	form.category_id = id
	categorySearch.value = selectedCategoryName.value
	categoryOptions.value = []
}

const clearCategory = () => {
	selectedCategoryId.value = null
	selectedCategoryName.value = ''
	categorySearch.value = ''
	form.category_id = null
	categoryOptions.value = []
}
const handleCreate = async () => {
	if (isSubmitting.value) return
	isSubmitting.value = true
	try {
		await createTicket({
			title: form.title,
			description: form.description,
			priority: form.priority,
			status: form.status,
			category_id: selectedCategoryId.value ?? null,
			submitter_id: form.submitter_id,
			assigned_to_id: form.assigned_to_id,
			attachments: attachments.value
		})
		await router.push('/user/tickets')
	} catch (error) {
		console.error('Failed to create ticket', error)
	} finally {
		isSubmitting.value = false
	}
}
</script>

<template>
	<UDashboardGroup>
		<Sidebar />
		<UDashboardPage class="flex flex-col flex-1 min-w-0 overflow-hidden">
			<Navbar title="Create ticket" icon="i-lucide-ticket-plus" class="w-full" />
			<div class="flex flex-col flex-1 gap-6 p-6 overflow-auto">
				<form class="flex flex-col gap-4 max-w-3xl w-full" @submit.prevent="handleCreate">
					<div class="grid grid-cols-1 gap-4 sm:grid-cols-2">
						<UFormGroup label="Title" name="title">
							<UInput v-model="form.title" placeholder="Ticket Title" required maxlength="255" />
						</UFormGroup>
						<UFormGroup label="Description" name="description" class="sm:col-span-2">
							<UTextarea v-model="form.description" placeholder="Ticket Description" rows="6" class="min-h-32 resize-vertical" maxlength="2000" />
							<p class="text-xs text-muted mt-1">Provide a clear, detailed description (max 2000 characters).</p>
						</UFormGroup>
                        <UFormGroup label="Priority" name="priority">
                            <USelect
                                v-model="form.priority"
                                :items="priorityOptions"
                                placeholder="Select priority"
                            />
                            </UFormGroup>

                            <!-- <UFormGroup label="Status" name="status">
                            <USelect
                                v-model="form.status"
                                :items="statusOptions"
                                placeholder="Select status"
                            />
                            </UFormGroup> -->

								<UFormGroup label="Category" name="category_id" class="sm:col-span-2">
										<div class="relative">
											<UInput
												:value="categorySearch"
												placeholder="Search categories by name"
												@input="onCategoryInput"
												aria-label="Search categories"
											/>
											<div v-if="categoryLoading" class="absolute right-2 top-2 text-xs text-muted">Searching...</div>
